@import url(https://fonts.googleapis.com/css2?family=Ubuntu&display=swap);:root{--section-c0:#4b4b4b;--section-c1:#595959;--section-c2:#333333}body.theme-fandomdesktop-dark{--section-c0:#4b4b4b;--section-c1:#595959;--section-c2:#333333;font-family:'Ubuntu'}body.theme-fandomdesktop-light{--section-c0:#e8e8e8;--section-c1:#d3d3d3;--section-c2:#bfbfbf;font-family:'Ubuntu'}.mainpage-section{background-color:var(--section-c1);border:0.2em solid var(--theme-accent-color);border-radius:0px;margin:1em 0em;padding:0em 1em;text-align:center}.button{background-color:var(--section-c0);width:15em;border:0.2em solid var(--theme-accent-color);border-radius:0px;padding:0.5em 1em;color:var(--theme-page-text-color);text-align:center;display:inline-block;transition-duration:0.2s}.button:hover{background-color:var(--section-c2)}.toc{background-color:var(--section-c1);border:0.2em solid var(--theme-accent-color);border-radius:4px;margin:0em 0.5em;float:right}.toclevel-1 ul{display:none}.hoverimage{transition:0.2s ease}.hoverimage a img:hover{opacity:0.8}.portable-infobox{background-color:var(--section-c1);width:45%;border:0.2em solid var(--theme-accent-color);border-radius:8px;padding:0.5em 0.5em;text-align:center;font-family:"Rubik"}.pi-image-thumbnail{margin:1em 0em}.fandom-table{background-color:var(--section-c1);border:0.2em solid var(--theme-accent-color);border-radius:0px;margin:1em 0em;padding:0.5em 0.5em;text-align:center}.fandom-table tr th{background-color:var(--section-c0)}